var funcB = function(b: B) { console.log(b.x); }
var a: A = new A();  var b: B = new B();
b.store.value = funcA  // OK, since {function(A)} <: {function(B)}
b.store.value = funcB  // OK.

var a2: A = b; // OK, since B is a subtype of A
a2.store.value( a ) // RUNTIME ERROR, the types are all correct, but remember b.store.value was assigned to funcB, which can only handle subtypes of B!

section.programlisting:
// assume this file to be contained in a package "myPackage"
enum Color {
    RED, GREEN, BLUE
}

enum Country {
    DE : "276",
    US : "840",
    TR : "792"
}

var red: Color = Color.RED;
var us: Country = Country.US;

console.log(red.name); // --> RED
console.log(red.value); // --> RED
console.log(red.n4class.fqn); // --> myPackage.Color
console.log(red.toString()); // --> RED

console.log(us.name); // --> US
console.log(us.value); // --> 840
console.log(us.n4classfqn); // --> myPackage.Country
console.log(us.toString()); // --> 840

N4EnumDeclaration <Yield>:
	=>(	{N4EnumDeclaration}
		(declaredModifiers+=N4Modifier)*
		'enum' name=BindingIdentifier<Yield>? )
	'{'
		(literals+=N4EnumLiteral (',' literals+=N4EnumLiteral)*)?
	'}';

N4EnumLiteral: name=IdentifierName (':' value=STRING)?;

requirement.programlisting:
/** * Base class for all enumeration, literals are assumed to be static constant fields of concrete subclasses. */ public object N4Enum {
/** * Returns the name of a concrete literal */ public get name(): string
/** * Returns the value of a concrete literal. If no value is * explicitly set, it is similar to the name. */ public get value(): string
/** * Returns a string representation of a concrete literal, it returns * the same result as value() */ public toString(): string
/** * Returns the meta class object of this enum literal for reflection. * The very same meta class object can be retrieved from the enumeration type directly. */ public static get n4type(): N4EnumType
//IDE-785 this as return type in static
/** * Returns array of concrete enum literals */ public static get literals(): Array<? extends this>
/** * Returns concrete enum literal that matches provided name, * if no match found returns undefined. */ public static findLiteralByName(name: string): this
/** * Returns concrete enum literal that matches provided value, * if no match found returns undefined. */ public static findLiteralByValue (value: string): this }

