ExportDeclaration: {ExportDeclaration} ExportDeclarationImpl ;
fragment ExportDeclarationImpl*: 'export' ( wildcardExport?='*' ExportFromClause Semi | ExportClause ->ExportFromClause? Semi | exportedElement=ExportableElement | defaultExport?='default' (->exportedElement=ExportableElement | defaultExportedExpression=AssignmentExpression<In=true,Yield=false> Semi) ) ;
fragment ExportFromClause*: 'from' reexportedFrom=[types::TModule|ModuleSpecifier] ;
fragment ExportClause*: '{' (namedExports+=ExportSpecifier (',' namedExports+=ExportSpecifier)* ','?)? '}' ;
ExportSpecifier: element=IdentifierRef<Yield=false> ('as' alias=IdentifierName)? ;
